import React, { Component } from 'react'
import logo from './images/logo.svg';
import './App.css';
import Login from './components/Login';
import Footer from './components/Footer';


class App extends Component {
	constructor(props) {
		super(props)

		this.state = {
			isLoggedin: false
		}
		this.checkIfLoggedIn = this.checkIfLoggedIn.bind(this)
		this.remomeUsername = this.remomeUsername.bind(this)
	}
	checkIfLoggedIn = () => {
		let isLogged = localStorage.getItem('username');
		this.setState({
			isLoggedin : isLogged ? true : false
		})
		console.log(this.state.isLoggedin);
	}
	remomeUsername = () => {
		localStorage.removeItem('username');
		this.checkIfLoggedIn();
	}
	componentDidMount(){
		this.checkIfLoggedIn();
	}
	render() {
		const currentScreen = this.state.isLoggedin ? <div><button className="btn btn-danger" onClick={this.remomeUsername}>Clear</button></div> : <Login checkIfLoggedIn={this.checkIfLoggedIn} />;
		return (
			<div className="App">
				<div className="logo__contain">
					<img src={logo} className="logo" />
					<span className="logo__text">onOne</span>
				</div>
				<div>
					{currentScreen}
				</div>
				<Footer></Footer>
			</div>
		)
	}
}

export default App
