import React, { Component } from 'react'
import logo from './images/logo.svg';
import './App.css';
import Login from './components/Login';
import Footer from './components/Footer';


class App extends Component {
	constructor(props) {
		super(props)

		this.state = {

		}
		this.renderInit = this.renderInit.bind(this)
		this.remomeUsername = this.remomeUsername.bind(this)
	}

	renderInit = () => {
		alert('jere');
		let isLogged = localStorage.getItem('username');
		console.log(isLogged);
		if(isLogged){
			return <div><button className="btn btn-danger" onClick={this.remomeUsername}>Clear</button></div>;
		}else{
			return <Login renderLogin={this.renderInit} />;
		}
	}
	remomeUsername = () => {
		localStorage.removeItem('username');
		this.renderInit();
	}
	render() {
		return (
			<div className="App">
				<div className="logo__contain">
					<img src={logo} className="logo" />
					<span className="logo__text">onOne</span>
				</div>
				<div>
					{this.renderInit()}
				</div>
				<Footer></Footer>
			</div>
		)
	}
}

export default App
