import React, { Component } from 'react'
import logo from './images/logo.svg';
import './App.css';
import Login from './components/Login';
import Main from './components/Main';
import Footer from './components/Footer';


class App extends Component {
	constructor(props) {
		super(props)

		this.state = {
			isLoggedin: false
		}
		this.checkIfLoggedIn = this.checkIfLoggedIn.bind(this)
		this.logout = this.logout.bind(this)
	}
	checkIfLoggedIn = () => {
		let isLogged = localStorage.getItem('username');
		this.setState({
			isLoggedin : isLogged ? true : false
		})
	}
	logout = () => {
		localStorage.removeItem('username');
		this.checkIfLoggedIn();
	}
	componentDidMount(){
		this.checkIfLoggedIn();
	}
	render() {
		const computedScreen = this.state.isLoggedin ? <Main logout={this.logout} /> : <Login checkIfLoggedIn={this.checkIfLoggedIn} />;
		return (
			<div className="App">
				<header className="header">
					<div className="logo__contain">
						<img src={logo} className="logo" />
						<span className="logo__text">onOne</span>
					</div>
					<div className="dropdown">
						<button className="btn btn-light dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
							<i className="fa fa-cog"></i>
						</button>
						<div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
							<a className="dropdown-item" href="#">Action</a>
							<a className="dropdown-item" href="#">Another action</a>
							<a className="dropdown-item" href="#">Something else here</a>
						</div>
					</div>
				</header>
				<div>
					{computedScreen}
				</div>
				<Footer></Footer>
			</div>
		)
	}
}

export default App
