import React, { Component } from 'react'
import logo from './images/logo.svg';
import './App.css';
import Login from './components/Login';
import Main from './components/Main';
import Footer from './components/Footer';
import {Dropdown, DropdownButton} from 'react-bootstrap';

class App extends Component {
	constructor(props) {
		super(props)

		this.state = {
			username: false,
			theme: localStorage.getItem('theme') ? localStorage.getItem('theme') : 'white'
		}
		this.checkIfLoggedIn = this.checkIfLoggedIn.bind(this)
		this.logout = this.logout.bind(this)
		this.renderOptions = this.renderOptions.bind(this)
		this.renderStyling = this.renderStyling.bind(this)
		this.setTheme = this.setTheme.bind(this)
	}
	checkIfLoggedIn = () => {
		let username = localStorage.getItem('username');
		this.setState({
			username : username
		})
	}
	logout = () => {
		localStorage.removeItem('username');
		this.checkIfLoggedIn();
	}
	renderOptions = () => {
		if (this.state.theme == 'white') {
			button = <Dropdown.Item onClick={ () => this.setTheme('dark')}>Dark theme</Dropdown.Item>;
		  } else {
			button = <LoginButton onClick={this.handleLoginClick} />;
		  }
		return(
			<Dropdown className="header__actions">
				<DropdownButton key="left" title={<i className="fas fa-users-cog"></i>} variant="light" id="dropdown-basic">
					<Dropdown.Item onClick={this.logout}>Logout</Dropdown.Item>
				</DropdownButton>
			</Dropdown>
		)
	}
	renderStyling(){
		let background = "";
		let color = "";
		switch (this.state.theme) {
			case 'white':
				background = "#fff";
				color = "";
				break;
			case 'dark':
				background = "#000";
				color = "#fff";
				break;
			default:
				break;
		}
		return({
			background:background,
			color:color
		})
	}
	setTheme(newTheme){
		this.setState({
			theme:newTheme
		}, () => {
			localStorage.setItem('theme',newTheme)
		})
	}
	componentDidMount(){
		this.checkIfLoggedIn();
	}
	render() {
		const computedScreen = this.state.username ? <Main username={this.state.username} /> : <Login checkIfLoggedIn={this.checkIfLoggedIn} />;
		const options = this.state.username ? this.renderOptions() : '';
		const styling = this.renderStyling();
		return (
			<div className="App" style={ styling }>
				<header className="header">
					<div className="logo__contain">
						<img src={logo} className="logo" />
						<span className="logo__text">onOne</span>
					</div>
					{ options }
				</header>
				<div>
					{ computedScreen }
				</div>
				<Footer></Footer>
			</div>
		)
	}
}

export default App
