import React, { Component } from 'react'
import '../css/main.css';
import {Modal, Button} from 'react-bootstrap';
import UserPage from './UserPage';
import CallPage from './CallPage';
import {Route, BrowserRouter as Router} from 'react-router-dom';

class Main extends Component {
	constructor(props) {
		super(props)

		this.state = {
			onCall:false
		}
	}
	startCall(){
		this.setState({
			onCall : true
		})
	}
	endCall(){
		this.setState({
			onCall : true
		})
	}
	render() {
		const {onCall} = this.state;
		const compCurrentScreen = onCall ?
		<CallPage startCall={this.startCall} endCall={this.endCall} username={this.props.username} />
		:
		<UserPage startCall={this.startCall} endCall={this.endCall} username={this.props.username} />;
		return (
			<Router>
				<Switch>
				<Route path="/" exact component={UserPage} />
				<Route path="/Call" exact component={CallPage} />

				</Switch>
				{/* compCurrentScreen */}
			</Router>
		)
	}
}

export default Main
