import React, { Component } from 'react'
import callImage from '../images/start_call.svg';
import '../css/main.css';

class Main extends Component {
	constructor(props) {
		super(props)

		this.state = {
			showModal:false
		}
		this.show = this.show.bind(this)
		this.close = this.close.bind(this)
	}
	show(){
		this.setState({
			showModal:true
		})
	}
	close(){
		this.setState({
			showModal:false
		})
	}
	render() {
		return (
			<div>
				<h2>Welcome <span className="label__link">{this.props.username}</span>.</h2>
				<img src={callImage} className="call__image" />
				<h3>
					Start a call or join a room
				</h3>
				<div>
					<button className="btn btn-primary">Start call</button>
					&nbsp;&nbsp;
					<button className="btn btn-light">Join room</button>
				</div>
				<Modal show={this.show} onHide={this.close}>
					<Modal.Header closeButton>
					<Modal.Title>Modal heading</Modal.Title>
					</Modal.Header>
					<Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
					<Modal.Footer>
					<Button variant="secondary" onClick={handleClose}>
						Close
					</Button>
					<Button variant="primary" onClick={handleClose}>
						Save Changes
					</Button>
					</Modal.Footer>
				</Modal>
			</div>
		)
	}
}

export default Main
