import React from 'react';
import logo from './images/logo.svg';
import './App.css';
import Login from './components/Login';
import Footer from './components/Footer';

function App() {
	const renderInit = () => {
		let isLogged = localStorage.getItem('username');
		if(isLogged){
			return <div>OK</div>;
		}else{
			return <Login renderLogin={renderInit} />;
		}
	}
	remomeUsername = () => {
		localStorage.setItem('username', this.state.username);
		this.props.renderLogin();
	}
	return (
		<div className="App">
			<div className="logo__contain">
				<img src={logo} className="logo" />
				<span className="logo__text">onOne</span>
			</div>
			<div>
				{renderInit()}
			</div>
			<Footer></Footer>
		</div>
		);
	}
export default App;
