import React, { Component } from 'react'
import loginImage from '../images/login.svg';
import '../css/login.css';

class Login extends Component {
	constructor(props) {
		super(props)

		this.state = {
			username: ''
		}
		this.onUsernameChange = this.onUsernameChange.bind(this)
		this.setUsername = this.setUsername.bind(this)
	}
	onUsernameChange = (event) => {
		this.setState({
			username:event.target.value
		})
	}
	setUsername = () => {
		localStorage.setItem('username', this.state.username);
		this.props.checkIfLoggedIn();
	}
	render() {
		const {username} = this.state;
		return (
			<div className="login">
				<img src={loginImage} className="login__logo" />
				<br/>
				<input type="text" value={username} onChange={this.onUsernameChange} className="form-control login__username bg-light border-0 small" placeholder="Enter your username" />
				<button onClick={this.setUsername} className="btn btn-primary">Continue</button>
			</div>
		)
	}
}

export default Login
