import React, { Component } from 'react'
import callImage from '../images/start_call.svg';
import '../css/main.css';
import {Modal, Button} from 'react-bootstrap';
import selfieImage from '../images/selfie.svg';

class Main extends Component {
	constructor(props) {
		super(props)

		this.state = {
			showModal:false
		}
		this.show = this.show.bind(this)
		this.close = this.close.bind(this)
	}
	show(){
		this.setState({
			showModal:true
		})
	}
	close(){
		this.setState({
			showModal:false
		})
	}
	render() {
		return (
			<div>
				<h2>Welcome <span className="label__link">{this.props.username}</span>.</h2>
				<img src={callImage} className="call__image" />
				<h3>
					Start a call or join a room
				</h3>
				<div>
					<button className="btn btn-primary" onClick={this.show}>Start call</button>
					&nbsp;&nbsp;
					<button className="btn btn-light">Join room</button>
				</div>
				<Modal show={this.state.showModal} onHide={this.close} size="lg">
					<Modal.Header closeButton>
						<Modal.Title></Modal.Title>
					</Modal.Header>
					<Modal.Body className="call__start-modal">
						<img className="call__start-img" src={selfieImage} />
						<input type="text" value={username} onChange={this.onUsernameChange} className="form-control login__username bg-light border-0 small" placeholder="Enter your username" />

					</Modal.Body>
					<Modal.Footer>
						<Button variant="secondary" onClick={this.close}>
							Close
						</Button>
						<Button variant="primary" onClick={this.close}>
							Start
						</Button>
					</Modal.Footer>
				</Modal>
			</div>
		)
	}
}

export default Main
